def import_csv():
    '''Asks the user for a csv file_name, returns a dictionary with a new animal list and matrix'''
    file_name = input("Enter file name (with extension): ")
    compatibility_matrix = []
    length = 0
    with open(file_name) as file:
        lines = file.readlines()
        for i in range(0, len(lines)):
            items = lines[i].split(',')
            items[len(items) - 1] = items[len(items) - 1].replace('\n', '')
            length = len(items)
            compatibility_matrix.append(items)
    blank = []
    for i in range(0, length):
        blank.append('')
    compatibility_matrix.insert(0, blank)
    print('New compatability matrix:')
    for item in compatibility_matrix:
        print(item)
    animal_list = {}
    for i in range(1,len(compatibility_matrix)):
        animal_list[i] = compatibility_matrix[i][0]
    print('New animal list:')
    for key in animal_list.keys():
        print(str(key) + ': ' + animal_list[key])
    data = {
        'compatability_matrix': compatibility_matrix,
        'animal_list': animal_list
    }
    return data


def recursive_search(matrix: list, matches: list, compare: int, cur_animal: int, cur_matches: list):
    '''Recursively searches through matrix to find a list of matches that have a greater compatability 
    rating than compare with the cur_animal and all animals in cur_matches'''
    print("Loading...")
    cur_matches.append(cur_animal)
    cur_matches.sort()
    for i in range(1, len(matrix[int(cur_animal)])):
        if matrix[int(cur_animal)][i] == '':
            pass
        elif i in cur_matches:
            pass
        elif int(matrix[int(cur_animal)][i]) >= compare:
            for j in range(0, len(cur_matches)):
                if matrix[cur_matches[j]][i] == '':
                    break
                if int(matrix[cur_matches[j]][i]) < compare:
                    break
                if j == len(cur_matches)-1 and int(matrix[cur_matches[j]][i]) >= compare:
                    if i not in cur_matches and cur_matches not in matches:
                        new_matches = cur_matches.copy()
                        recursive_search(matrix, matches, compare, i, new_matches)
    if cur_matches not in matches:
        matches.append(cur_matches)


